mapper.update:
UPDATE t_webim_user
username = #{username}, nickname = #{nickname}, password = #{password}, mobile = #{mobile}, email = #{email}, avatar = #{avatar}, signature = #{signature}, hx_username = #{hxUsername}, hx_password = #{hxPassword},
WHERE id = #{userId}

mapper.select:

SELECT id, username, password, nickname, email, mobile, avatar, signature, create_time, update_time, latest_login_time, available, hx_username, hx_password
    FROM t_webim_user t
        WHERE t.id = #{userId}


SELECT id, username, password, nickname, email, mobile, avatar, signature, create_time, update_time, latest_login_time, available, hx_username, hx_password
    FROM t_webim_user t
        WHERE t.username = #{userAccount}
           OR t.email = #{userAccount}


	AND available = ${available}


SELECT id, username, password, nickname, email, mobile, avatar, signature, create_time, update_time, latest_login_time, available, hx_username, hx_password
    FROM t_webim_user t
        WHERE t.username = #{userAccount}
           OR t.email = #{userAccount}
